function getBrowserLocales(e={}){const n={languageCodeOnly:false};const a={...n,...e};const t=navigator.languages===undefined?[navigator.language]:navigator.languages;if(!t){return undefined}return t.map((e=>{const n=e.trim();return a.languageCodeOnly?n.split(/-|_/)[0]:n}))}function displayLangBanner(){const e=getBrowserLocales({languageCodeOnly:true})[0];const n=localStorage.getItem("lang-mode");if(e==document.documentElement.lang||n==="manual"){return}else if(e=="en"){const e=document.querySelector('[data-lang="fr"]');e.classList.add("is-active")}else if(e=="fr"){const e=document.querySelector('[data-lang="en"]');e.classList.add("is-active")}}function dismissLangBanner(){const e=document.querySelector(".lang-banner");e.addEventListener("click",(e=>{if(e.target.closest("[data-lang-dismiss]")){localStorage.setItem("lang-mode","manual");const e=document.querySelector("[data-lang].is-active");e.classList.remove("is-active")}}))}displayLangBanner();dismissLangBanner();